SET NAMES UTF8;
DROP DATABASE IF EXISTS pureshop;
CREATE DATABASE pureshop CHARSET=UTF8;
USE pureshop;
#网站基本信息表
CREATE TABLE pureshop_web_info(
  web_name VARCHAR(16),
  logo VARCHAR(64),
  admin_email VARCHAR(64),
  icp VARCHAR(32),
  copyright VARCHAR(64)

);


#导航表
CREATE TABLE pureshop_nav(
    nid INT PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(16),
    url VARCHAR(128),
    title VARCHAR(32),
    parent INT #设置二级菜单
);

/**女装产品**/
CREATE TABLE pureshop_product_woman(
  wid INT PRIMARY KEY AUTO_INCREMENT,
  type VARCHAR(16),           #所属分类 
  title VARCHAR(128),         #标题
  price DECIMAL(10,2),        #价格
  score DECIMAL(2,1),          #评分
  sold_count INT,             #累积销量
  stock VARCHAR(64),          #库存
  show_pic VARCHAR(128),          #展示图片路径
  lg1 VARCHAR(128),           #大图片路径
  lg2 VARCHAR(128),
  lg3 VARCHAR(128),
  lg4 VARCHAR(128),
  lg5 VARCHAR(128),            
  md1 VARCHAR(128),          #中图片路径   
  md2 VARCHAR(128),            
  md3 VARCHAR(128),            
  md4 VARCHAR(128),            
  md5 VARCHAR(128),            
  sm1 VARCHAR(128),           #小图片路径
  sm2 VARCHAR(128),            
  sm3 VARCHAR(128),            
  sm4 VARCHAR(128),            
  sm5 VARCHAR(128),
  xg1 VARCHAR(128),          #效果图
  xg2 VARCHAR(128)
);


/**用户信息**/
CREATE TABLE pureshop_user(
  uid INT PRIMARY KEY AUTO_INCREMENT,
  uname VARCHAR(32),
  upwd VARCHAR(32),
  email VARCHAR(64),
  phone VARCHAR(16),
  avatar VARCHAR(128),        #头像图片路径
  user_name VARCHAR(32),      #用户名，如王小明
  gender INT                  #性别  0-女  1-男
);

/**收货地址信息**/
CREATE TABLE pureshop_receiver_address(
  aid INT PRIMARY KEY AUTO_INCREMENT,
  user_id INT,                #用户编号
  receiver VARCHAR(16),       #接收人姓名
  province VARCHAR(16),       #省
  city VARCHAR(16),           #市
  county VARCHAR(16),         #县
  address VARCHAR(128),       #详细地址
  cellphone VARCHAR(16),      #手机
  fixedphone VARCHAR(16),     #固定电话
  postcode CHAR(6),           #邮编
  tag VARCHAR(16),            #标签名

  is_default BOOLEAN          #是否为当前用户的默认收货地址
);

/**购物车条目**/
CREATE TABLE pureshop_shoppingcart(
  iid INT PRIMARY KEY AUTO_INCREMENT,
  user_id INT,      #用户编号
  product_id INT,   #商品编号
  count INT,        #购买数量
  is_checked BOOLEAN #是否已勾选，确定购买
);

/**用户订单**/
CREATE TABLE pureshop_order(
  aid INT PRIMARY KEY AUTO_INCREMENT,
  user_id INT,
  address_id INT,
  status INT,             #订单状态  1-等待付款  2-等待发货  3-运输中  4-已签收  5-已取消
  order_time BIGINT,      #下单时间
  pay_time BIGINT,        #付款时间
  deliver_time BIGINT,    #发货时间
  received_time BIGINT    #签收时间
)AUTO_INCREMENT=10000000;

/**用户订单**/
CREATE TABLE pureshop_order_detail(
  did INT PRIMARY KEY AUTO_INCREMENT,
  order_id INT,           #订单编号
  product_id INT,         #产品编号
  count INT               #购买数量
);

/****首页轮播广告商品****/
CREATE TABLE pureshop_index_carousel(
  cid INT PRIMARY KEY AUTO_INCREMENT,
  img VARCHAR(128),
  title VARCHAR(64)
);


